import img7 from "../../assets/images/avatar7.png";
import eye from "../../assets/images/eyeActive.svg";
import Button from "../reusable_component/Button";
import { useState, useEffect } from "react";

const Featured = () => {
  const [currentDate, setCurrentDate] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentDate(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const hours = currentDate.getHours();
  const minutes = currentDate.getMinutes();
  const seconds = currentDate.getSeconds();
  return (
    <section id="featured-section" className="container">
      <div className="cta-left">
        <div className="featured-user">
          <div>
            <img src={img7} alt="Shroomie" />
          </div>
          <p>Shroomie</p>
        </div>
        <h1 className="cta-header">Magic Mushrooms</h1>
        <div style={{ alignSelf: "flex-start" }}>
          <Button
            padding={"1.9rem 5rem"}
            altText={"eye icon"}
            imgSrc={eye}
            btnName={"See NFT"}
            bgColor={"#FFFFFF"}
            color={"#2B2B2B"}
          />
        </div>
      </div>
      <div className="auction-timer">
        <p>Auction ends in:</p>
        <div className="time">
          <div className="time-labels">
            <h1>59</h1>
            <p>Hours</p>
          </div>
          <span>:</span>
          <div className="time-labels">
            <h1>59</h1>
            <p>Minutes</p>
          </div>
          <span>:</span>
          <div className="time-labels">
            <h1>59</h1>
            <p>Seconds</p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Featured;
