import img1 from "../../assets/images/howitworks1.png";
import img2 from "../../assets/images/howitworks2.png";
import img3 from "../../assets/images/howitworks3.png";

const HowItWorks = () => {
  const cardStyle = {
    display: "flex",
    flexDirection: "column",
    flex: "1",
  };

  return (
    <section id="how-it-works" className="container">
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          marginBottom: "6rem",
        }}
      >
        <div>
          <h2 className="secondary-header">How It Works</h2>
          <p className="collection-after-text">Find out how to get started</p>
        </div>

        <div style={{ display: "flex", gap: "3rem" }}>
          <div style={cardStyle}>
            <div>
              <img src={img1} alt="how-it-works img1" />
            </div>
            <div>
              <h3>Setup Your Wallet</h3>
              <p>
                Set up your wallet of choice. Connect it to the Animarket by
                clicking the wallet icon in the top right corner.
              </p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HowItWorks;
